package loopingdetails;

public class Looping {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=0;
		while(i<5)
		{
			System.out.println("the value of i is " +i);
			i=i+1;
			
		}
		System.out.println("end the value of i");
		


	}

}


